# Directory to save the SVGs
achievements_dir = 'static/images/svg/achievements'

# Create directory if it doesn't exist
os.makedirs(achievements_dir, exist_ok=True)

# Save each SVG to a file
for filename, svg_content in achievement_icons.items():
    filepath = os.path.join(achievements_dir, filename)
    with open(filepath, 'w') as f:
        f.write(svg_content)
    print(f'Created/updated: {filepath}')

print('\nAll achievement icons have been updated!')
